import React, { useState } from 'react';
import './Allocate.css'


function AllocateSub() {

    const [selectedOption, setSelectedOption] = useState('');
    const [teacherOptions, setTeacherOptions] = useState([]);
  
    useEffect(() => {
      // Make an API request to fetch the list of teachers
      axios.get('https://localhost:7190/api/subjects') 
        .then((response) => {
          // Assuming the API returns an array of teacher names, update the state
          setTeacherOptions(response.data);
        })
        .catch((error) => {
          console.error('Error fetching teacher data:', error);
        });
    }, []);
    
    const handleOptionChange = (e) => {
      setSelectedOption(e.target.value);
    };
  return (
    <div>
        {/* upper section */}

        <div className='uppermain'>
        <div className='upper'>
            <fieldset className='field'>
                <legend>Teacher Details</legend>
                <table className='trow'>
                <tr ><td>Username</td>
                <td >
     <select className='drop1' placeholder='Select Teacher' value={selectedOption} onChange={handleOptionChange}>
        {options.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))}
      </select>
      <button className='button'  type="button" >Save</button>
      </td>
      </tr>
                </table>

            </fieldset>
        </div>
        </div>

       {/* lower section */}

        <div className='uppermain'>
        <div className='lower'>
            <fieldset className='field'>
                <legend>Allocate Subjects</legend>
                <table className='trow'>
                <tr ><td>Username</td>
                <td >
     <select className='drop1' placeholder='Select Teacher' value={selectedOption} onChange={handleOptionChange}>
        {options.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))}
      </select>
      <button className='button'  type="button" >Allocate</button>
      </td>
      </tr>
                </table>
                <div className='uppermain'>
                    <div className='table-container'>
                    <table className='table'>
                    <thead>
                        <tr>
                            <th>Subjects</th>
                            <th>Actions</th>
                        </tr>
                        <tr>
                            <td>Science</td>
                            <td>Actions</td>
                        </tr>
                    </thead>
                    </table>
                    </div>

                </div>

            </fieldset>
        </div>
        </div>

    </div>
  )
}

export default AllocateSub
