import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Allocate.css'


function AllocateSub() {

    const [selectedOption, setSelectedOption] = useState('');
    const [teacherOptions, setTeacherOptions] = useState([]);

    const [selectedOption1, setSelectedOption1] = useState('');
    const [subjectOptions, setSubjectOptions] = useState([]);

    const [allocatedSubjects, setAllocatedSubjects] = useState([]);
  
//   get teachers
    useEffect(() => {
      // Make an API request to fetch the list of teachers
      axios.get('https://localhost:7190/api/teacher') 
        .then((response) => {
          // Assuming the API returns an array of teacher names, update the state
          
          setTeacherOptions(response.data);

          if (response.data.length > 0) {
            setSelectedOption(`${response.data[0].firstName} ${response.data[0].lastName}`);
          }
        })
        .catch((error) => {
          console.error('Error fetching teacher data:', error);
        });
    }, []);



    useEffect(() => {
        if (selectedOption) {
          // Make an API request to fetch subjects for the selected teacher
          axios.get(`https://localhost:7190/api/allocatesub/${selectedOption}`)
            .then((response) => {
              // Assuming the API returns an array of subjects for the selected teacher, update the allocatedSubjects state
              const subjectsArray = response.data.reduce((acc, item) => {
                acc[item.allocateSubID] = item.subjects.split(',').map((subject) => subject.trim());
                return acc;
              }, {});
              setAllocatedSubjects(subjectsArray);
              console.log(allocatedSubjects)
            })
            .catch((error) => {
              console.error('Error fetching subjects for teacher:', error);
            });
        }
      }, [selectedOption]);
      
      


      const handleOptionChange = (e) => {
        setSelectedOption(e.target.value);
      };

      const handleMsg = () => {
        
        alert(selectedOption1)
      };
// get subjects

useEffect(() => {
    // Make an API request to fetch the list of teachers
    axios.get('https://localhost:7190/api/subjects') 
      .then((response) => {
        // Assuming the API returns an array of teacher names, update the state
        setSubjectOptions(response.data);

        if (response.data.length > 0) {
            setSelectedOption1(`${response.data[0].subjectName}`);
          }
      })
      .catch((error) => {
        console.error('Error fetching teacher data:', error);
      });
  }, []);

  const handleOptionChange1 = (e) => {
    setSelectedOption1(e.target.value);
  };

  const handleAllocate = () => {
    if (selectedOption1) {
      // Check if the selected subject is already allocated
      const isDuplicate = Object.values(allocatedSubjects).some(
        (subjectsArray) => subjectsArray.includes(selectedOption1)
      );
  
      if (!isDuplicate) {
        // Generate a new allocateSubID
        const allocateSubID = Object.keys(allocatedSubjects).length + 1;
  
        // Add the selected subject to the allocatedSubjects object
        setAllocatedSubjects({
          ...allocatedSubjects,
          [allocateSubID]: [selectedOption1],
        });
      }
    }
  };
  
  
  const handleDeallocate = (allocateSubID, subject) => {
    const updatedAllocatedSubjects = { ...allocatedSubjects };
  
    // Check if the allocateSubID exists in the allocatedSubjects
    if (updatedAllocatedSubjects.hasOwnProperty(allocateSubID)) {
      // Remove the selected subject from the subjects array associated with allocateSubID
      updatedAllocatedSubjects[allocateSubID] = updatedAllocatedSubjects[allocateSubID].filter(
        (s) => s !== subject
      );
  
      // If the subjects array is empty after removal, delete the allocateSubID
      if (updatedAllocatedSubjects[allocateSubID].length === 0) {
        delete updatedAllocatedSubjects[allocateSubID];
      }
  
      // Update the state with the modified object
      setAllocatedSubjects(updatedAllocatedSubjects);
    }
  };
  
  
  



  return (
    <div>
        {/* upper section */}

        <div className='uppermain'>
        <div className='upper'>
            <fieldset className='field'>
                <legend>Teacher Details</legend>
                <table className='trow'>
                <tr ><td>Username</td>
                <td >
                <select
        className='drop1'
        placeholder='Select Teacher'
        value={selectedOption}
        onChange={handleOptionChange}
      >
        {teacherOptions.map((option, index) => (
          <option key={index} value={`${option.firstName} ${option.lastName}`}>
            {`${option.firstName} ${option.lastName}`}
          </option>
        ))}
      </select>
      <button className='button'  type="button" onClick={handleMsg} >Save</button>
      </td>
      </tr>
                </table>

            </fieldset>
        </div>
        </div>

       {/* lower section */}

        <div className='uppermain'>
        <div className='lower'>
            <fieldset className='field'>
                <legend>Allocate Subjects</legend>
                <table className='trow'>
                <tr ><td>Username</td>
                <td >
     <select className='drop1' placeholder='Select Teacher' value={selectedOption1} onChange={handleOptionChange1}>
        {subjectOptions.map((option, index) => (
          <option key={index} value={option.subjectName}>
            {option.subjectName}
          </option>
        ))}
      </select>
      <button className='button'  type="button" onClick={handleAllocate} >Allocate</button>
      </td>
      </tr>
                </table>
                <div className='uppermain'>
                    <div className='table-container'>
                    <table className='table'>
                    <thead>
                        <tr>
                            <th>Subjects</th>
                            <th>Actions</th>
                        </tr>
                    </thead>

                    <tbody>
  {Object.keys(allocatedSubjects).map((allocateSubID) => (
    allocatedSubjects[allocateSubID].map((subject, index) => (
      <tr key={allocateSubID + index}>
        <td>{subject}</td>
        <td>
          <button
            className='button1'
            onClick={() => handleDeallocate(allocateSubID, subject)}
          >
            Deallocate
          </button>
        </td>
      </tr>
    ))
  ))}
</tbody>


                    </table>
                    </div>

                </div>

            </fieldset>
        </div>
        </div>

    </div>
  )
}

export default AllocateSub
