import React, { useState } from 'react';
import './Allocate.css'


function AllocateSub() {

    const [selectedOption, setSelectedOption] = useState(null);
    const options = ['Option 1', 'Option 2', 'Option 3', 'Option 4'];
  
    const handleOptionChange = (e) => {
      setSelectedOption(e.target.value);
    };
  return (
    <div>
        <div className='uppermain'>
        <div className='upper'>
            <fieldset className='field'>
                <legend>Teacher Details</legend>
                <table>
                <tr className='trow'><td>Username</td>
                <td>
     <select className='drop1' value={selectedOption} onChange={handleOptionChange}>
        {options.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))}
      </select>
      <button className='button'  type="button" >Save</button>
      </td>
      </tr>
                </table>

            </fieldset>
        </div>
        </div>

    </div>
  )
}

export default AllocateSub
